import (
	context "context"
	rapiddocs "github.com/oauth-client-credentials-custom/rapiddocs"
	rapiddocsclient "github.com/oauth-client-credentials-custom/rapiddocs/client"
)

client := rapiddocsclient.NewClient()
response, err := client.Auth.GetTokenWithClientCredentials(
	context.TODO(),
	&rapiddocs.GetTokenRequest{
		Cid:      "cid",
		Csr:      "csr",
		Scp:      "scp",
		EntityId: "entity_id",
	},
)

import (
	context "context"
	rapiddocs "github.com/oauth-client-credentials-custom/rapiddocs"
	rapiddocsclient "github.com/oauth-client-credentials-custom/rapiddocs/client"
)

client := rapiddocsclient.NewClient()
response, err := client.Auth.RefreshToken(
	context.TODO(),
	&rapiddocs.RefreshTokenRequest{
		ClientId:     "client_id",
		ClientSecret: "client_secret",
		RefreshToken: "refresh_token",
	},
)
