import (
	context "context"
	rapiddocs "github.com/oauth-client-credentials-default/rapiddocs"
	rapiddocsclient "github.com/oauth-client-credentials-default/rapiddocs/client"
)

client := rapiddocsclient.NewClient()
response, err := client.Auth.GetToken(
	context.TODO(),
	&rapiddocs.GetTokenRequest{
		ClientId:     "client_id",
		ClientSecret: "client_secret",
	},
)
