import (
	auth "github.com/oauth-client-credentials-nested-root/rapiddocs/auth"
	context "context"
	rapiddocsclient "github.com/oauth-client-credentials-nested-root/rapiddocs/client"
)

client := rapiddocsclient.NewClient()
response, err := client.Auth.GetToken(
	context.TODO(),
	&auth.GetTokenRequest{
		ClientId:     "client_id",
		ClientSecret: "client_secret",
	},
)
